import {

    FETCH_PRODUCTS_START,
    FETCH_PRODUCTS_SUCCESS,
    FETCH_PRODUCTS_FAIL,

    LOAD_MORE_PRODUCTS_START,
    LOAD_MORE_PRODUCTS_SUCCESS,
    LOAD_MORE_PRODUCTS_FAIL,

    FETCH_PRODUCT_BY_ID_START,
    FETCH_PRODUCT_BY_ID_SUCCESS,
    FETCH_PRODUCT_BY_ID_FAIL,

    ADD_PRODUCT_TO_BASKET,

    SEARCH_PRODUCT,

    FETCH_CATEGORIES_START,
    FETCH_CATEGORIES_SUCCESS,
    FETCH_CATEGORIES_FAIL,

} from 'actionTypes'

import {getRenderedProductsLength} from 'selectors'

import {
    fetchProducts as fetchProductsApi,
    loadMoreProducts as loadMoreProductsApi,
    fetchProductById as fetchProductByIdApi,
    fetchCategories as fetchCategoriesApi,
} from 'api'

export const fetchProducts = () => async dispatch => {
    dispatch({type: FETCH_PRODUCTS_START});

    try {
        const products = await fetchProductsApi();
        dispatch({
            type: FETCH_PRODUCTS_SUCCESS,
            payload: products,
        })
    }
    catch(err) {
        dispatch({
            type: FETCH_PRODUCTS_FAIL,
            payload: err,
            error: true,
        })
    }
};

export const loadMoreProducts = () => async (dispatch, getState) => {
    const offset = getRenderedProductsLength(getState());

    dispatch({type: LOAD_MORE_PRODUCTS_START});

    try {
        const products = await loadMoreProductsApi(offset);
        dispatch({
            type: LOAD_MORE_PRODUCTS_SUCCESS,
            payload: products,
        })
    }
    catch(err) {
        dispatch({
            type: LOAD_MORE_PRODUCTS_FAIL,
            payload: err,
            error: true,
        })
    }
};


export const fetchProductById = id => async dispatch => {
    dispatch({type: FETCH_PRODUCT_BY_ID_START});

    try {
        const product = await fetchProductByIdApi(id);
        dispatch({
            type: FETCH_PRODUCT_BY_ID_SUCCESS,
            payload: product,
        })
    }
    catch(err) {
        dispatch({
            type: FETCH_PRODUCT_BY_ID_FAIL,
            payload: err,
            error: true,
        })
    }
};

export const addProductToBasket = id => dispatch => {
  dispatch({
      type: ADD_PRODUCT_TO_BASKET,
      payload: id
  })
};

export const searchProduct = text => dispatch => {
  dispatch({
      type: SEARCH_PRODUCT,
      payload: text
  })
};


export const fetchCategories = () => async dispatch => {
    dispatch({type: FETCH_CATEGORIES_START});

    try {
        const categories = await fetchCategoriesApi();
        dispatch({
            type: FETCH_CATEGORIES_SUCCESS,
            payload: categories,
        })
    }
    catch(err) {
        dispatch({
            type: FETCH_CATEGORIES_FAIL,
            payload: err,
            error: true,
        })
    }
};