import React, {Component} from 'react'
import {connect} from 'react-redux'
import {Link} from 'react-router'
import R from 'ramda'

import {
    fetchProducts,
    loadMoreProducts,
    addProductToBasket,
    fetchCategories
} from 'actions'
import {getProducts} from 'selectors'

class Products extends Component {

    componentDidMount() {
        this.props.fetchProducts();
        this.props.fetchCategories();
    }

    renderProduct(product, index) {
        const {addProductToBasket} = this.props;
        const shortDescription = `${R.take(60, product.description)}...`;

        return (
            <div className="col-sm-4 col-md-4 col-lg-4 book-list" key={index}>
                <div className="thumbnail">
                    <img src={product.image}
                         alt={product.name}
                         className="img-thumbnail"
                    />
                    <div className="caption">

                        <h4 className="pull-right">${product.price}</h4>
                        <h4>
                            <Link to={`/products/${product.id}`}>
                                {product.name}
                            </Link>
                        </h4>
                        <p>{shortDescription}</p>
                        <p className="itemButton">
                            <button
                                onClick={()=>addProductToBasket(product.id)}
                                className="btn btn-success">
                                Buy now!

                            </button>

                            <Link to={`/product/${product.id}`} className="btn btn-info">
                                More info
                            </Link>
                        </p>
                    </div>
                </div>
            </div>
        )
    }

    render() {
        const {products, loadMoreProducts} = this.props;

        return(
            <div>
                <div className="books row">
                    {products.map((product,index)=>this.renderProduct(product,index))}
                </div>
                <div className="row">
                    <div className="col-md-12">
                        <button
                            onClick={loadMoreProducts}
                            className="pull-left btn btn-danger"
                        >Load more</button>
                    </div>
                </div>
            </div>
        )
    }
};

const mapStateToProps = (state, ownProps) => ({
   products: getProducts(state, ownProps)
});

const mapDispatchToProps = {
    fetchProducts,
    loadMoreProducts,
    addProductToBasket,
    fetchCategories
};

export default connect(mapStateToProps, mapDispatchToProps)(Products)